<?php

namespace Backend\AdminBundle\Repository;

/**
 * ComplexRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ComplexRepository extends \Doctrine\ORM\EntityRepository
{

    public function getApiFaqs( $complexId )
    {
        $qb = $this->createQueryBuilder('a');

        $qb->select('a, b')
            ->innerJoin('a.business', 'b')
            ->where('a.enabled = 1')
            ->andWhere('b.enabled = 1')
            ->andWhere('a.id = :complexId')
            ->setParameter('complexId', $complexId)
        ;

        return $qb->getQuery()->getOneOrNullResult();
    }


    public function getComplexByUser($userID)
    {

        $sql = "	SELECT  c.id, c.name
					FROM    user_complex uc
					    INNER JOIN complex c ON(c.id = uc.complex_id)
					WHERE   uc.user_id = {$userID}
					ORDER BY c.name";

        $stmt = $this->getEntityManager()->getConnection()->prepare($sql);
        $stmt->execute();

        $execute = $stmt->fetchAll();
        $arrReturn = array();
        foreach ($execute as $row) {
            $arrReturn[$row["name"]] = $row["id"];
        }

        return $arrReturn;

    }


    public function getRequiredDTData($start, $length, $orders, $search, $columns, $filterComplex, $businessLocale, $role)
    {
        //print "entra";die;
        // Create Main Query
        $query = $this->createQueryBuilder('e');

        // Create Count Query
        $countQuery = $this->createQueryBuilder('e');
        $countQuery->select('COUNT(e)');

        //ENABLED
        if($role != 'SUPER ADMIN'){
            $query->andWhere("e.enabled = 1");
            $countQuery->andWhere("e.enabled = 1");

        }


        // Create inner joins

        //complex Type
        $query->join('e.complexType', 't');
        $countQuery->join('e.complexType', 't');

        //geoState
        $query->join('e.geoState', 's');
        $countQuery->join('e.geoState', 's');


        //var_dump($filterComplex);die;

        if ($filterComplex != null) {
            $query->andWhere('e.id IN (:arrComplexID)')->setParameter('arrComplexID', $filterComplex);
            $countQuery->andWhere('e.id IN (:arrComplexID)')->setParameter('arrComplexID', $filterComplex);
        }


        // Fields Search
        foreach ($columns as $key => $column) {
            if ($column['search']['value'] != '') {
                // $searchItem is what we are looking for
                $searchItem = $column['search']['value'];
                $searchQuery = null;

                switch ($column['name']) {
                    case 'id':
                        {
                            $searchQuery = 'e.id =' . $searchItem;
                            break;
                        }
                    case 'name':
                        {
                            $searchQuery = 'e.name LIKE \'%' . $searchItem . '%\'';
                            break;
                        }
                    case 'city':
                        {
                            $searchQuery = 's.name LIKE \'%' . $searchItem . '%\'';
                            break;
                        }
                    case 'zipCode':
                        {
                            $searchQuery = 'e.zipCode LIKE \'%' . $searchItem . '%\'';
                            break;
                        }
                    case 'complexType':
                        {
                            if ($businessLocale == "es") {
                                $searchQuery = 't.nameES LIKE \'%' . $searchItem . '%\'';
                            } else {
                                $searchQuery = 't.nameEN LIKE \'%' . $searchItem . '%\'';
                            }

                            break;
                        }


                }


                if ($searchQuery !== null) {
                    $query->andWhere($searchQuery);
                    $countQuery->andWhere($searchQuery);
                }
            }
        }

        // Limit
        $query->setFirstResult($start)->setMaxResults($length);

        // Order
        // Orders
        foreach ($orders as $key => $order) {
            // Orders does not contain the name of the column, but its number,
            // so add the name so we can handle it just like the $columns array
            $orders[$key]['name'] = $columns[$order['column']]['name'];
        }

        foreach ($orders as $key => $order) {

            // $order['name'] is the name of the order column as sent by the JS
            if ($order['name'] != '') {
                $orderColumn = null;

                switch ($order['name']) {
                    case 'id':
                        {
                            $orderColumn = 'e.id';
                            break;
                        }
                    case 'name':
                        {
                            $orderColumn = 'e.name';
                            break;
                        }
                    case 'city':
                        {
                            $orderColumn = 's.name';
                            break;
                        }
                    case 'zipCode':
                        {
                            $orderColumn = 'e.code';
                            break;
                        }
                    case 'complexType':
                        {
                            if ($businessLocale == "es") {
                                $orderColumn = 't.nameES';
                            } else {
                                $orderColumn = 't.nameEN';
                            }

                            break;
                        }

                }

                if ($orderColumn !== null) {
                    $query->orderBy($orderColumn, $order['dir']);
                }
            }
        }


        $results = $query->getQuery()->getResult();
        $countResult = $countQuery->getQuery()->getSingleScalarResult();

        return array(
            "results" => $results,
            "countResult" => $countResult
        );
    }


