        //print $sql;die;

        $stmt = $this->getEntityManager()->getConnection()->prepare($sql);
        $stmt->execute();

        $execute = $stmt->fetchAll();

        //var_dump($execute);die;

        $arrReturn = array();

        foreach($execute as $c){
            //$date = strtotime($c["created_at"]);
            //$createdDay = $this->jjg_calculate_next_month($date);
            //var_dump($createdDay);die;

            //if day is same day send invoice
            //$today = gmdate("d");
            //var_dump($today);die;

            //if($createdDay == $today){
                //PRINT "entraa".$createdDay."==".$today;

                $complexID = $c["id"];
                //var_dump($complexID);die;
                $properties = intval($this->getPropertiesQuantityByComplex($complexID));

                ///get FEE for this complex on the agreement plan
                $myFee = $this->getComplexFee($properties);
                if(intval($myFee) > 0){
                    $arrReturn[$complexID] = array();
                    $arrReturn[$complexID]["country_id"] = intval($c["country_id"]);
                    $arrReturn[$complexID]["fee"] = $myFee;
                    $arrReturn[$complexID]["customer_id"] = $c["customer_id"];
                    $arrReturn[$complexID]["complex_name"] = $c["complex_name"];
                    $arrReturn[$complexID]["properties_quantity"] = $properties;


                }
            //}
        }

        //var_dump($arrReturn);die;
        return $arrReturn;

    }


    public function getComplexFee($quantity){

        $sql = "    SELECT fee
                    FROM	agreement_plan
                    WHERE	{$quantity} >= property_limit_from AND {$quantity} <= property_limit_to";


        //print $sql;die;
        $stmt = $this->getEntityManager()->getConnection()->prepare($sql);
        $stmt->execute();

        $execute = $stmt->fetchAll();

        //var_dump($execute);die;

        if(!empty($execute)){
            $myFee = floatval($execute[0]["fee"]);
            return $myFee;
        }
        else{
            return 0;
        }

    }


    public function getPropertiesQuantityByComplex($complexID){

        $sql = "    SELECT COUNT(p.id) quantity
                    FROM	property p
                        INNER JOIN complex_sector s ON (p.complex_sector_id = s.id)
                        INNER JOIN complex c ON (s.complex_id = c.id)
                        AND c.id = {$complexID}
                    WHERE	p.enabled = 1";

        $stmt = $this->getEntityManager()->getConnection()->prepare($sql);
        $stmt->execute();

        $execute = $stmt->fetchAll();

        if(!empty($execute)){
            return $execute[0]["quantity"];
        }
        else{
            return 1;
        }


    }


    /**
     * Function to calculate the same day one month in the future.
     *
     * This is necessary because some months don't have 29, 30, or 31 days. If the
     * next month doesn't have as many days as this month, the anniversary will be
     * moved up to the last day of the next month.
     *
     * @param $start_date (optional)
     *   UNIX timestamp of the date from which you'd like to start. If not given,
     *   will default to current time.
     *
     * @return $timestamp
     *   UNIX timestamp of the same day or last day of next month.
     */
    public function jjg_calculate_next_month($start_date = FALSE)
    {
        if ($start_date) {
            $now = $start_date; // Use supplied start date.
        } else {
            $now = time(); // Use current time.
        }

        // Get the current month (as integer).
        $current_month = date('n', $now);

        // If the we're in Dec (12), set current month to Jan (1), add 1 to year.
        if ($current_month == 12) {
            $next_month = 1;
            $plus_one_month = mktime(0, 0, 0, 1, date('d', $now), date('Y', $now) + 1);
        } // Otherwise, add a month to the next month and calculate the date.
        else {
            $next_month = $current_month + 1;
            $plus_one_month = mktime(0, 0, 0, date('m', $now) + 1, date('d', $now), date('Y', $now));
        }

        $i = 1;
        // Go back a day at a time until we get the last day next month.
        while (date('n', $plus_one_month) != $next_month) {
            $plus_one_month = mktime(0, 0, 0, date('m', $now) + 1, date('d', $now) - $i, date('Y', $now));
            $i++;
        }

        return gmdate("d", $plus_one_month);

    }



    public function getComplexAdmins($complexID)
    {


        $sql = "	SELECT  u.id, u.username, u.name
                    FROM    user_complex uc
                   	    INNER JOIN user u ON (uc.user_id = u.id)
                    WHERE     uc.complex_id = {$complexID}
                    AND     u.role_id IN (1,2)
                    ";
        //ROLE ID = 2 COMPLEX ADMINISTRATOR
        //ROLE ID = 1 BUSINESS ADMINISTRATOR

        //print $sql;die;

        $stmt = $this->getEntityManager()->getConnection()->prepare($sql);
        $stmt->execute();

        $execute = $stmt->fetchAll();

        $arrReturn = array();
        foreach($execute as $u){
            $arrReturn[$u["id"]] = $u["username"];
        }

        return $arrReturn;

    }

}
