<?php

namespace Backend\AdminBundle\Repository;

use Doctrine\ORM\Query\Expr\Join;

/**
 * PollQuestionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PollQuestionRepository extends \Doctrine\ORM\EntityRepository
{

    public function getApiAnswer($pollQuestionId) {
        $qb = $this->createQueryBuilder('a');

        $qb->select('a, pqt')
            ->leftJoin('a.pollQuestionType', 'pqt', Join::WITH, $qb->expr()->andX(
                $qb->expr()->eq('pqt', 'a.pollQuestionType'),
                $qb->expr()->eq('pqt.enabled', '1')
            ))
            ->where('a.enabled = 1')
            ->andWhere('a.id = :poll_question_id')
            ->setParameter('poll_question_id', $pollQuestionId)
        ;

        return $qb->getQuery()->getOneOrNullResult();
    }

    public function getApiPoll($pollId)
    {

        $qb = $this->createQueryBuilder('a');

        $qb->select('a, p, pqt')
            ->leftJoin('a.poll', 'p', Join::WITH, $qb->expr()->andX(
                $qb->expr()->eq('p', 'a.poll'),
                $qb->expr()->eq('p.enabled', '1')
            ))
            ->leftJoin('a.pollQuestionType', 'pqt', Join::WITH, $qb->expr()->andX(
                $qb->expr()->eq('pqt', 'a.pollQuestionType'),
                $qb->expr()->eq('pqt.enabled', '1')
            ))
            ->where('a.enabled = 1')
            ->andWhere('p.id = :poll_id')
            ->setParameter('poll_id', $pollId)
        ;

        return $qb->getQuery()->getResult();


    }

    public function clearQuestions($pollID){

        $sql = "   SELECT  question.id questionID,  
	                        qoption.id qoptionID
					FROM    poll p    
					    LEFT JOIN poll_question question ON(question.poll_id = p.id)
					    LEFT JOIN poll_question_option qoption ON (question.id = qoption.poll_question_id)
					WHERE   p.id = {$pollID}
					AND     p.enabled = 1
					ORDER BY question.id, qoption.id";


        $stmt = $this->getEntityManager()->getConnection()->prepare($sql);
        $stmt->execute();

        $execute = $stmt->fetchAll();


        //clear options
        foreach ($execute as $q){
            if($q["qoptionID"] != NULL){
                $delete = "    DELETE FROM poll_question_option WHERE id = ".intval($q["qoptionID"]);
                $stmt = $this->getEntityManager()->getConnection()->prepare($delete);
                $stmt->execute();
            }
        }

        //clear questions
        foreach ($execute as $q){
            $delete = "    DELETE FROM poll_question WHERE id = ".intval($q["questionID"]);
            $stmt = $this->getEntityManager()->getConnection()->prepare($delete);
            $stmt->execute();

        }

    }




}
